import React, { useRef } from "react";
import emailjs from "@emailjs/browser";
import toast from "react-hot-toast";

export default function Contact() {
  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm(
        "service_kkcsw0n",
        "template_v227996",
        form.current,
        "od7vdCp_fSgg0OjOR"
      )
      .then(
        (result) => {
          console.log(result.text);
          console.log("message sent");
          toast.success("Successfully toasted!");
          e.target.reset();
        },
        (error) => {
          console.log(error.text);
          toast.error("Error sending message.");
        }
      );
  };
  return (
    <div className=" bg-black p-8 font-serif">
      <h1 className="text-4xl text-white text-center p-8">Contact</h1>
      <form
        className="flex flex-col space-y-2 items-start"
        ref={form}
        onSubmit={sendEmail}
      >
        <label className="">Name</label>
        <input
          className="border-4 border-indigo-200 border-b-indigo-500 p-2 w-full"
          type="text"
          name="user_name"
          placeholder="Enter your name"
        />
        <label>Email</label>
        <input
          className="border border-sky-500 rounded-lg p-2 w-full"
          type="email"
          name="user_email"
          placeholder="Enter your Email"
          required
        />
        <label>Message</label>
        <textarea
          className="border border-sky-500 rounded-lg p-2 w-full"
          name="message"
          placeholder="Enter your message"
        />
        <input
          className=" bg-blue-500 p-4 rounded-lg text-white"
          type="submit"
          value="Send"
        />
      </form>
    </div>
  );
}
