import React, { useRef } from "react";
import emailjs from "@emailjs/browser";
import toast from "react-hot-toast";

export default function Contact() {
  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm(
        "service_u53lwup",
        "template_v227996",
        form.current,
        "AnObA2XWVRXnAYdiG"
      )
      .then(
        (result) => {
          console.log(result.text);
          toast.success("Message successfully sent!");
          e.target.reset();
        },
        (error) => {
          console.log(error.text);
          toast.error("Error sending message.");
        }
      );
  };
  return (
    <div className="p-8 font-serif">
      <h1 className="text-4xl text-center p-8">Contact</h1>
      <form
        className="flex flex-col space-y-2 items-start"
        ref={form}
        onSubmit={sendEmail}
      >
        <label className="">Name</label>
        <input
          required
          className=" outline-none focus:ring-1 border-4 border-indigo-200 border-b-indigo-500 hover:border-indigo-500/100 p-2 w-full"
          type="text"
          name="user_name"
          placeholder="Enter your name"
        />
        <label>Email</label>
        <input
          className="outline-none border-4 border-indigo-200 border-b-indigo-500 hover:border-indigo-500/100 p-2 w-full
      invalid:border-4 invalid:text-pink-600
      focus:invalid:border-b-pink-500 focus:invalid:border-indigo-200 focus:invalid:ring-pink-500"
          type="email"
          name="user_email"
          placeholder="Enter your Email"
          required
        />
        <label>Message</label>
        <textarea
          className="outline-none focus:ring-1 border-4 border-indigo-200 border-b-indigo-500 hover:border-indigo-500/100 p-2 w-full"
          name="message"
          placeholder="Enter your message"
        />
        <input
          className="focus:border-indigo-500 focus:ring-2 focus:ring-indigo-500 bg-blue-500 p-4 text-white"
          type="submit"
          value="Send"
        />
      </form>
    </div>
  );
}
